var e={COMMON:{BROWSER_NOT_SUPPORTED:"The web browser you are using does not support some functionality, such as saving and loading reports. You can continue to use the Report Tool with limited functionality. Update or use a different web browser to make use of all functionality of this report tool.",WARNING_BEFORE_UNLOAD:"The data you entered is not automatically saved. It will be lost if you close this web browser window. Use the 'Save' link (Ctrl + S or ⌘ S) that is at the top of all pages to save your data in a file locally on your computer, and reload it later to continue working on it.",MORE_INFO:"For more information, see <a href={{url}}>{{name}}</a>.",HIDE_MESSAGE:"Hide message",BTN_INFO:"Info expand",BTN_CLOSE_INFO:"Close info",YES:"Yes",NO:"No",CLICK_EXPAND:"Click to show section",CLICK_COLLAPSE:"Click to hide section",TO_TOP:"Back to top",ACTIVE:"Active"},EARL:{PASSED:"Passed",FAILED:"Failed",CANT_TELL:"Cannot tell",NOT_PRESENT:"Not present",NOT_CHECKED:"Not checked",LEVEL_A:"Level A",LEVEL_AA:"Level AA",LEVEL_AAA:"Level AAA"},ERROR:{TITLE:"Oops! Something went wrong",INTRO:"This is not the page you are looking for. Try one of these solutions to solve the problem.",SUGGESTION1:"If you typed the URL, make sure it is exactly what it should be.",SUGGESTION2:"Go <a href={{link_back}}>back to the previous page</a> and try again.",SUGGESTION3:"Make sure the files you are working from follow the <a href={{link_format}}>wcag-em format</a>.",BUG_REPORT:"To report any bugs or feature suggestions, leave an issue on our <a href={{link_github}}>Github project page</a>."},IMPORT:{TITLE:"Import a WCAG EARL report",INTRO:"If you have used another application to generate a WCAG EARL-report that falls into the scope of this evaluation, you may be able to import these and add them to the evaluation audit result. The file itself should be: JSON-LD parseable, consist of objects in EARL format and evaluation tests should be related to WCAG.",LABEL_SELECT_FILE:"Select a JSON-LD file"},MENUBAR:{MENU:"Menu",MENU_IMPORT:"Import",MENU_IMPORT_TITLE:"Start EARL report import wizzard",MENU_NEW:"New Report",MENU_OPEN:"Open",MENU_SAVE:"Save",MENU_SAVE_TITLE:"Ctrl + S or ⌘ S",MENU_RESOURCES:"Key Resources"},NAV:{MENU:"Menu",MENU_IMPORT:"Import",MENU_IMPORT_TITLE:"Start EARL report import wizzard",MENU_NEW:"New Report",MENU_OPEN:"Open",MENU_SAVE:"Save",MENU_SAVE_TITLE:"Ctrl + S or ⌘ S",MENU_RESOURCES:"Key Resources",MENU_LANGUAGE:"Language",WZRD_LABEL:"Evaluation steps",WZRD_START:"Start",WZRD_SCOPE:"1. Define Scope",WZRD_EXPLORE:"2. Explore Website",WZRD_SAMPLE:"3. Select Sample",WZRD_AUDIT:"4. Audit Sample",WZRD_REPORT:"5. Report Findings",WZRD_ACTIVE:"Active",WZRD_VIEWREPORT:"View Report",PREV_STEP:"Previous step",NEXT_STEP:"Next step",STEP_START:"Start",STEP_SCOPE:"Define Scope",STEP_EXPLORE:"Explore Website",STEP_SAMPLE:"Select Sample",STEP_AUDIT:"Audit Sample",STEP_REPORT:"Report Findings",STEP_VIEWREPORT:"View Report",BTN_BACK_TO_EVAL:"Back"},OPEN:{TITLE:"Open Evaluation Report",INTRO:"To reload a saved file, click the 'Find and select the data file' button and locate the JSON file that you previously saved.",LABEL_SELECT_FILE:"Find and select the data file",MSG_LOADING:"Loading the evaluation, please wait...",BTN_LOAD_FILE:"Load data from selected file"},REPORT:{BY:"Report Creator:",COMMISION_BY:"Evaluation Commissioner:",HD_SUMMARY:"Summary of the evaluation findings",HD_SCOPE:"Scope of the evaluation",LABEL_SITE_NAME:"Website name",LABEL_SITE_SCOPE:"Scope of the website",LABEL_CONFORMANCE_TGT:"Conformance target",LABEL_EXTRA_REQUIREMENTS:"Additional evaluation requirements",LABEL_SUPPORT_BASE:"Accessibility support baseline",LABEL_RELIEDUP_TECH:"Relied upon technologies",HD_SCORE:"Overview of audit results",RESULTS_OF:"Results of",PRINCIPLE:"Principle",TOTAL_SCORE:"Total",HD_CRITERIA_REPORT:"Detailed audit results",LABEL_DESCR:"Findings",HD_SAMPLE:"Sample of audited web pages",HD_SPECIFICS:"Recording of evaluation specifics",HD_DOCS:"Related WCAG 2 resources"},SAVE:{TITLE:"Save Evaluation Report",INTRO:"Use the link below to save the evaluation report information that you have entered in a JSON data file locally on your computer.",BTN_DOWNLOAD_DATA_FILE:"Save data file locally to your computer",TIPS:"You can save partially-complete reports (and notes) and work on them later. You can open them from any web browser and you can transfer the data file to another computer. <br> You can Save periodically as you work to avoid losing data if your web browser closes. You can use Windows shortcut keys Ctrl+S or Mac shortcut keys ⌘S to open the Save dialog (or automatically save it in your downloads folder depending on your web browser settings). <br> When you are done entering your evaluation report information, you can download an HTML file of the completed report from the <a href={{link_report}}>View Report</a> page."}},t={AUDIT:{ASSERTION_RESULT_DESCRIPTION_LABEL:"Observations",ASSERTION_RESULT_DESCRIPTION_PLACEHOLDER:"Observations made during evaluation",BTN_COLLAPSE_PAGES:"Hide web pages to enter individual results",BTN_COMPLETE_SELECTED:"Set selected to complete",BTN_EXPAND_PAGES:"Show web pages to enter individual results",BTN_OPEN_SELECTED:"Open selected pages",BTN_SHOW_TEXT:"Show criterion text",BTN_UNCOMPLETE_SELECTED:"Set selected to incomplete",CLICK_TO_DELETE:"Click to delete",FILTER_LEVEL_LEGEND:"Show conformance level",FILTER_NEW_IN_WCAG21:"Added in WCAG 2.1",FILTER_VERSION_LEGEND:"Show version",FILTER:"Show",HD_CRITERIA:"Success Criteria to Evaluate",HD_SAMPLE_SELECT:"Sample to Evaluate",HOW_TO:"How to meet",INF_AUDIT_CRITERIA:"This section lists the WCAG 2 success criteria. Use the filter to show or hide success criteria of different levels (A, AA and AAA). You can select results as: 'Not checked', 'Passed', 'Failed', 'Not present', and 'Cannot tell'; and you can provide details, comments, or other observations made during evaluation in the accompanying text box.",INF_AUDIT_SAMPLE:"This section lists the web pages you selected in the previous step. The web pages that are checked in this section are listed when 'Show web pages to enter individual results' is activated in the 'Success Criteria to Evaluate' section. You can show an individual web page that you are evaluating, or show several web pages at the same time. <br> The chainlink icon opens the web page in a separate browser window.",INTRO:"Record the outcome from evaluating the web pages selected in the previous step. Compare the results between the structured page and randomly selected pages, and if needed, adjust the selected sample in the previous step. More guidance on this step is provided in <a href={{link_em_4}}>WCAG-EM Step 4: Audit the Selected Sample</a>. <br> <strong>Note:</strong> For each WCAG 2 success criteria, you can enter 'Results for the entire sample' and you can enter results for individual web pages. You can choose to enter either or both. To enter individual results, select the web page(s) under 'Sample to Evaluate' (in the left column); then under the specific success criteria, select 'Show web pages to enter individual results'",LABEL_OUTCOME:"Outcome",LABEL_PAGE_HANDLE:"Short page name",NO_PAGE_SELECTED:"No pages selected under Sample to Evaluate",NO_SAMPLE:"No sample available. Create a sample in step 2 and step 3.",NOTE:"Note",PRINCIPLE:"Principle",RESULTS_FOR:"Results for",SAMPLE_FINDINGS:"Results for the entire sample",SELECT_ALL:"Select all web pages",TESTED:"Tested",TITLE:"Step 4: Audit the Selected Sample",UNDERSTAND:"Understanding",UNTESTED:"{{critCount}} untested"},EXPLORE:{TITLE:"Step 2: Explore the Target Website",INTRO:"Explore the website to understand its purpose, functionality, and use. This step helps you determine which web pages to use for the evaluation in the next steps. Identify the web technologies used to provide the website and, if you want, take notes on other aspects of the website. Usually it is best to get input from the website owners and developers for this step. More guidance on this step is provided in <a href={{link_em_2}}>WCAG-EM Step 2: Explore the Target Website</a>.",HD_RELIEDUP_TECH:"Web Technologies Relied Upon",INF_RELIEDUP_TECH:"Identify the web technologies <a href={{link_relied_upon}}>relied upon according to WCAG 2</a> to provide the website. For more information, see <a href={{link_em_2d}}>WCAG-EM Step 2.d: Identify Web Technologies Relied Upon</a>.<br> Note: To add other technologies, select 'Others' and use the 'Web Technology' and 'Specification Address (URL)' field. The 'Specification Address (URL)' field should identify the web technology specification.",LABEL_TECH:"Web Technology",LABEL_TECH_SPEC:"Specification Address (URL)",PLH_TECH:"E.g. HTML5, CSS, DOM",PLH_TECH_SPEC:"Address (URL) or description for the web technology specification",BTN_REMOVE_TECH:"Remove technology",BTN_ADD_TECH:"Add web technology",HD_NOTE_TAKING:"Optional Exploration Notes",INF_NOTE_TAKING:"This section lets you record information during your website exploration. These notes are provided in the next step, to help you select a sample of web pages for evaluation. <strong>These notes are not included in the final report.</strong> They are saved when you save the file, and available when you later reload the file",LABEL_ESSENT_FUNC:"Essential functionality of the website",INF_ESSENT_FUNC:"You can use this field to take notes about essential functionality of the website. Examples of essential functionality include:<br> 'selecting and purchasing a product from the shop area of the website'<br> 'completing and submitting a form provided on the website'<br> 'registering for an account on the website'<br> For more information, see <a href={{link_em_2b}}>WCAG-EM Step 2.b: Identify Essential Functionality of the Website</a>.",LABEL_VARIETY_PAGE_TYPES:"Variety of web page types",INF_VARIETY_PAGE_TYPES:"You can use this field to take notes about the types (as opposed to instances) of web pages that you find on the web site. This includes notes about different styles, layouts, structures, and functionality provided on the website. For more information, see <a href={{link_em_2c}}>WCAG-EM Step 2.c: Identify the Variety of Web Page Types</a>.<br> Note: 'Web pages' include different 'web page states'; see definition of <a href={{link_page_state}}>web page states</a>.",LABEL_OTHER:"Others..."},REPORT:{TITLE:"Website Accessibility Evaluation Report",INTRO:"Below is the evaluation report based on the input that you provided in previous steps. You can go back to previous steps now and change any of this information. You can download the evaluation data so that you can work on it later. (This is the same as the 'Save' functionality.) <br> This report is intended to be downloaded. You can download the Evaluation Report HTML and CSS files below. You can then change the report content and visual design to meet your needs.",DOWNLOAD_REPORT:"Download and customize this report",BTN_SAVE_HTML:"Download the evaluation report (HTML)",BTN_SAVE_CSS:"Download the report stylesheet (CSS)",BTN_SAVE_JSON:"Save the evaluation data (JSON)"},SAMPLE:{TITLE:"Step 3: Select a Representative Sample",INTRO:"Select web pages for evaluation. The notes that you took during exploration in the previous step are provided here, to help you select the web pages. Ensure that the web pages selected are representative of the target website. More guidance on this step is provided in <a href={{link_em_3}}>WCAG-EM Step 3: Select a Representative Sample</a>.",HD_STRUCT_SAMPLE:"Structured Sample",HD_ESSENT_FUNC:"Essential functionality (optional notes from previous step)",HD_VARIETY_PAGE_TYPES:"Variety of web page types (optional notes from previous step)",HD_STRUCT_SAMPLE_SUB:"Structured Sample Web Pages",INF_STRUCT_SAMPLE:"Select web pages that reflect all identified (1) common web pages, (2) essential functionality, (3) types of web pages, (4) web technologies relied upon, and (5) other relevant web pages. For more information, see <a href={{link_em_3a}}>WCAG-EM Step 3.a: Include a Structured Sample</a>.<br> Note: 'Web pages' include different 'web page states'; see definition of <a href={{link_em_state}}>web page states</a>.",HD_RANDOM_SAMPLE:"Randomly Selected Sample",INF_RAND_SAMPLE:"Randomly select sample web pages; select 10% of the structured sample selected above. For more information, see <a href={{link_em_3b}}>WCAG-EM Step 3.b: Include a Randomly Selected Sample</a>.<br> Note: 'Web pages' include different 'web page states'; see definition of <a href={{link_em_state}}>web page states</a>.",RAND_SAMPLE_LENGTH:"Based on your structured sample of {{total}} web pages, chose at least {{count}} randomly selected web pages (to meet the 10% requirement in WCAG-EM).",LABEL_HANDLE:"Short name",LABEL_PAGE:"Address (URL) or description",NO_PAGES_DEFINED:"No pages defined for this list",ITEM:"Web page",PLH_TITLE:"Identifier for the web page",PLH_PAGE_URL:"Address (URL) or description of how to get there",BTN_ADD_PAGE:"Add web page",BTN_REMOVE_PAGE:"Remove page",STRUCTURED_PAGE:"Structured page",RANDOM_PAGE:"Random page",SAMPLE_PAGE:"Sample page"},SCOPE:{TITLE:"Step 1: Define the Evaluation Scope",INTRO:"Define the overall parameters and scope of the evaluation. Ideally, this is done with the person who commissioned the evaluation (who may or may not be the website owner), to ensure common expectations about the scope of the evaluation. More guidance on this step is provided in <a href={{link_step1}}>WCAG-EM Step 1: Define the Evaluation Scope</a>.",LABEL_SITE_NAME:"Website name",INF_SITE_NAME:"Provide a name for the website that you would like to evaluate. For example:<br> 'Public Website of Example Organization'<br> 'Webshop of Example Company'<br> 'Intranet of Example University'",LABEL_SITE_SCOPE:"Scope of the website",INF_SITE_SCOPE_0:"Define the scope of the website, so it is clear which web pages are included in the evaluation. For example:",INF_SITE_SCOPE_LI0:"'All web content of the public website of Example Org. located at http://www.example.org'",INF_SITE_SCOPE_LI1:"'All web content of the online shop of Example Org. located at http://www.example.org/shop/'",INF_SITE_SCOPE_LI2:"'All web content of the mobile version of the public website of Example Org. located at http://m.example.org'",INF_SITE_SCOPE_1:"WCAG-EM Step 1.a: Define the Scope of the Website",LABEL_WCAG_VERSION:"WCAG Version",INFO_WCAG_VERSION:"Select the WCAG version to use. Version 2.1 (default) or 2.0",WCAG21:"WCAG 2.1",WCAG20:"WCAG 2.0",LABEL_CONFORMANCE_TGT:"Conformance target",INF_CONF_TGT:"Select a target WCAG 2 conformance level ('A', 'AA', or 'AAA') for the evaluation. For more information, see <a href={{link_em_1b}}>WCAG-EM Step 1.b: Define the Conformance Target</a>. This selection determines which conformance level filters are active by default in 'step 4: Audit the Sample'.",LABEL_SUPPORT_BASE:"Accessibility support baseline",INF_SUPPORT_BASE:"Define the web browsers, assistive technologies, and other <a href={{link_user_agent}}>user agents</a> that will be <a href={{link_access_supp}}>accessibility supported</a> by the website. For example, 'Internet Explorer (IE) with JAWS', 'FireFox with NVDA', and 'Apple with VoiceOver' could be basic definitions. For more information, see <a href={{link_em_step1c}}>WCAG-EM Step 1.c: Define an Accessibility Support Baseline</a>.",LABEL_EXTRA_REQUIREMENTS:"Additional evaluation requirements",INF_EXTRA_REQUIREMENTS_0:"Define any additional evaluation requirements. For example:",INF_EXTRA_REQUIREMENTS_LI0:"'The report will include a list of all errors identified by the evaluator, rather than examples only'",INF_EXTRA_REQUIREMENTS_LI1:"'The report will include a description of the problem and repair suggestions for any errors listed'",INF_EXTRA_REQUIREMENTS_LI2:"'The evaluation will cover all web pages and web content of the website, rather than a selected sample only'",INF_EXTRA_REQUIREMENTS_1:"WCAG-EM Step 1.d: Define Additional Evaluation Requirements"},START:{TITLE:"WCAG-EM Report Tool",SUBTITLE:"Website Accessibility Evaluation Report Generator",INTRO_HD:"What this tool does",INTRO_1:"This tool helps you generate a report according to the {link_wcagem}. It does not perform any accessibility checks. It helps you follow the steps of WCAG-EM, to generate a {link_report_example} from the input that you provide. It is designed for experienced evaluators who know the {link_wcag} and are somewhat familiar with WCAG-EM. For an introduction to WCAG-EM, see the {link_wcagem_overview}",INTRO_2:"Note: This tool does not automatically save the information that you enter. To save your data in a file locally on your computer, use Windows shortcut keys Ctrl+S or Mac shortcut keys {{mac}} to open the Save dialog. (Or the 'Save' link at the top of the page will open the Save Evaluation Report page and from there the 'Save data file locally to your computer' link will open the Save dialog.)",USAGE_HD:"How this tool works",USAGE_LI1:"All functionality provided by this tool is now loaded and running locally in your web browser. You don't need an Internet connection beyond this point. When you close your web browser window, any unsaved data is lost.",USAGE_LI2:"All input that you provide through this tool is recorded as JSON data in the background (in your web browser, not on any server). You can Save periodically as you work to avoid losing data if your web browser closes.",USAGE_LI3:"You can save partially-complete reports and work on them later. To reload a saved file, use the 'Open' link at the top and find the file that you previously saved.",USAGE_LI4:"Links that are not part of the navigation or functionality (links to external resources) open in a new web browser windows.",TIPS_HD:"Tips for using this tool",TIPS_LI1:"You can go back and forth between the steps in any order. None of the fields are required.",TIPS_LI2:"To get more information about a field, select the {{info}} icon next to the field label.",TIPS_LI3:"The tool provides your report as HTML and CSS files. You can download these files from the 'View Report' page. You can then change the report content and visual design.",TIPS_LI4:"You can include in your report WCAG 2 success criteria beyond the conformance target. For example, the website is only required to meet Level AA, yet you want to also include Level AAA success criteria in the report. In step 4, use the level filter to show higher level criteria. Any criteria with a result will always be included in the report."},SUMMARY:{TITLE:"Step 5: Report the Evaluation Findings",INTRO:"Provide additional information about the evaluation that you want included in the report. The evaluation findings that you entered in the previous step are included below under 'Detailed audit results' for review and to help you write the executive summary. More guidance on this step is provided in <a href={{link_em_5}}>WCAG-EM Step 5: Report the Evaluation Findings</a>.",LABEL_TITLE:"Report title",TITLE_PREFIX:"Report for",INF_TITLE:"Provide a title for the website accessibility evaluation report. For example:<br> 'Evaluation Report for Example Organization'<br> 'Webshop Accessibility Analysis and Repair Suggestions'",LABEL_COMMISSIONER:"Evaluation commissioner",INF_COMMISSIONER:"The person, team of people, organization, in-house department, or other entity that commissioned the evaluation.",LABEL_CREATOR:"Evaluator",INF_CREATOR:"The person, team of people, organization, in-house department, or other entity responsible for carrying out the evaluation.",LABEL_DATE:"Evaluation date",INF_DATE:"Provide the completion date or duration dates of this evaluation. You can use any date format.",LABEL_SUMMARY:"Executive summary",INF_SUMMARY:"Provide a brief summary of the evaluation findings to give an overview on the results. For example, describe the overall accessibility of the website and key observations you made during the evaluation, such as frequently occurring issues and patterns.",LABEL_SPECIFICS:"Record of evaluation specifics (optional)",INF_SPECIFICS:"WCAG-EM suggests that you archive the web pages audited. For more information, see <a href={{link_em_5b}}>WCAG-EM Step 5.b: Record the Evaluation Specifics</a>. You can use this text field to record the evaluation tools, web browsers, assistive technologies, other software, and methods used for the evaluation. What you enter here will be included in the generated report. After you download the report, you could delete or edit this information in the HTML file before submitting the report.",HD_CRITERIA_REPORT:"Detailed audit results"}},o={COMMON:{CONFORMANCE_LEVEL:"Level"},WCAG21:{PRINCIPLE:{1:{ID:"perceivable",TITLE:"Perceivable",DESCRIPTION:"Information and user interface components must be presentable to users in ways they can perceive."},2:{ID:"operable",TITLE:"Operable",DESCRIPTION:"User interface components and navigation must be operable."},3:{ID:"understandable",TITLE:"Understandable",DESCRIPTION:"Information and the operation of user interface must be understandable."},4:{ID:"robust",TITLE:"Robust",DESCRIPTION:"Content must be robust enough that it can be interpreted by by a wide variety of user agents, including assistive technologies."}},GUIDELINE:{1.1:{ID:"text-alternatives",TITLE:"Text Alternatives",DESCRIPTION:"Provide text alternatives for any non-text content so that it can be changed into other forms people need, such as large print, braille, speech, symbols or simpler language."},1.2:{ID:"time-based-media",TITLE:"Timebased Media",DESCRIPTION:"Provide alternatives for time-based media."},1.3:{ID:"adaptable",TITLE:"Adaptable",DESCRIPTION:"Create content that can be presented in different ways (for example simpler layout) without losing information or structure."},1.4:{ID:"distinguishable",TITLE:"Distinguishable",DESCRIPTION:"Make it easier for users to see and hear content including separating foreground from background."},2.1:{ID:"keyboard-accessible",TITLE:"Keyboard Accessible",DESCRIPTION:"Make all functionality available from a keyboard."},2.2:{ID:"enough-time",TITLE:"Enough Time",DESCRIPTION:"Provide users enough time to read and use content."},2.3:{ID:"seizures-and-physical-reactions",TITLE:"Seizures and Physical Reactions",DESCRIPTION:"Do not design content in a way that is known to cause seizures or physical reactions."},2.4:{ID:"navigable",TITLE:"Navigable",DESCRIPTION:"Provide ways to help users navigate, find content, and determine where they are."},2.5:{ID:"input-modalities",TITLE:"Input Modalities",DESCRIPTION:"Make it easier for users to operate functionality through various inputs beyond keyboard."},3.1:{ID:"readable",TITLE:"Readable",DESCRIPTION:"Make text content readable and understandable."},3.2:{ID:"predictable",TITLE:"Predictable",DESCRIPTION:"Make Web pages appear and operate in predictable ways."},3.3:{ID:"input-assistance",TITLE:"Input Assistance",DESCRIPTION:"Help users avoid and correct mistakes."},4.1:{ID:"compatible",TITLE:"Compatible",DESCRIPTION:"Maximize compatibility with current and future user agents, including assistive technologies."}},SUCCESS_CRITERION:{"1.1.1":{ID:"non-text-content",TITLE:"Non-text Content",DESCRIPTION:"All non-text content that is presented to the user has a text alternative that serves the equivalent purpose, except for the situations listed below.",DETAILS:{DETAIL_0:{TITLE:"Controls, Input",DESCRIPTION:"If non-text content is a control or accepts user input, then it has a name that describes its purpose. (Refer to Success Criterion 4.1.2 for additional requirements for controls and content that accepts user input.)"},DETAIL_1:{TITLE:"Time-Based Media",DESCRIPTION:"If non-text content is time-based media, then text alternatives at least provide descriptive identification of the non-text content. (Refer to Guideline 1.2 for additional requirements for media.)"},DETAIL_2:{TITLE:"Test",DESCRIPTION:"If non-text content is a test or exercise that would be invalid if presented in text, then text alternatives at least provide descriptive identification of the non-text content."},DETAIL_3:{TITLE:"Sensory",DESCRIPTION:"If non-text content is primarily intended to create a specific sensory experience, then text alternatives at least provide descriptive identification of the non-text content."},DETAIL_4:{TITLE:"CAPTCHA",DESCRIPTION:"If the purpose of non-text content is to confirm that content is being accessed by a person rather than a computer, then text alternatives that identify and describe the purpose of the non-text content are provided, and alternative forms of CAPTCHA using output modes for different types of sensory perception are provided to accommodate different disabilities."},DETAIL_5:{TITLE:"Decoration, Formatting, Invisible",DESCRIPTION:"If non-text content is pure decoration, is used only for visual formatting, or is not presented to users, then it is implemented in a way that it can be ignored by assistive technology."}}},"1.2.1":{ID:"audio-only-and-video-only-prerecorded",TITLE:"Audio-only and Video-only (Prerecorded)",DESCRIPTION:"For prerecorded audio-only and prerecorded video-only media, the following are true, except when the audio or video is a media alternative for text and is clearly labeled as such:",DETAILS:{DETAIL_0:{TITLE:"Prerecorded Audio-only",DESCRIPTION:"An alternative for time-based media is provided that presents equivalent information for prerecorded audio-only content."},DETAIL_1:{TITLE:"Prerecorded Video-only",DESCRIPTION:"Either an alternative for time-based media or an audio track is provided that presents equivalent information for prerecorded video-only content."}}},"1.2.2":{ID:"captions-prerecorded",TITLE:"Captions (Prerecorded)",DESCRIPTION:"Captions are provided for all prerecorded audio content in synchronized media, except when the media is a media alternative for text and is clearly labeled as such.",DETAILS:{}},"1.2.3":{ID:"audio-description-or-media-alternative-prerecorded",TITLE:"Audio Description or Media Alternative (Prerecorded)",DESCRIPTION:"An alternative for time-based media or audio description of the prerecorded video content is provided for synchronized media, except when the media is a media alternative for text and is clearly labeled as such.",DETAILS:{}},"1.2.4":{ID:"captions-live",TITLE:"Captions (Live)",DESCRIPTION:"Captions are provided for all live audio content in synchronized media.",DETAILS:{}},"1.2.5":{ID:"audio-description-prerecorded",TITLE:"Audio Description (Prerecorded)",DESCRIPTION:"Audio description is provided for all prerecorded video content in synchronized media.",DETAILS:{}},"1.2.6":{ID:"sign-language-prerecorded",TITLE:"Sign Language (Prerecorded)",DESCRIPTION:"Sign language interpretation is provided for all prerecorded audio content in synchronized media.",DETAILS:{}},"1.2.7":{ID:"extended-audio-description-prerecorded",TITLE:"Extended Audio Description (Prerecorded)",DESCRIPTION:"Where pauses in foreground audio are insufficient to allow audio descriptions to convey the sense of the video, extended audio description is provided for all prerecorded video content in synchronized media.",DETAILS:{}},"1.2.8":{ID:"media-alternative-prerecorded",TITLE:"Media Alternative (Prerecorded)",DESCRIPTION:"An alternative for time-based media is provided for all prerecorded synchronized media and for all prerecorded video-only media.",DETAILS:{}},"1.2.9":{ID:"audio-only-live",TITLE:"Audio-only (Live)",DESCRIPTION:"An alternative for time-based media that presents equivalent information for live audio-only content is provided.",DETAILS:{}},"1.3.1":{ID:"info-and-relationships",TITLE:"Info and Relationships",DESCRIPTION:"Information, structure, and relationships conveyed through presentation can be programmatically determined or are available in text.",DETAILS:{}},"1.3.2":{ID:"meaningful-sequence",TITLE:"Meaningful Sequence",DESCRIPTION:"When the sequence in which content is presented affects its meaning, a correct reading sequence can be programmatically determined.",DETAILS:{}},"1.3.3":{ID:"sensory-characteristics",TITLE:"Sensory Characteristics",DESCRIPTION:"Instructions provided for understanding and operating content do not rely solely on sensory characteristics of components such as shape, color, size, visual location, orientation, or sound.",DETAILS:{}},"1.3.4":{ID:"orientation",TITLE:"Orientation",DESCRIPTION:"Content does not restrict its view and operation to a single display orientation, such as portrait or landscape, unless a specific display orientation is essential.",DETAILS:{}},"1.3.5":{ID:"identify-input-purpose",TITLE:"Identify Input Purpose",DESCRIPTION:"The purpose of each input field collecting information about the user can be programmatically determined when:",DETAILS:{}},"1.3.6":{ID:"identify-purpose",TITLE:"Identify Purpose",DESCRIPTION:"In content implemented using markup languages, the purpose of User Interface Components, icons, and regions can be programmatically determined.",DETAILS:{}},"1.4.1":{ID:"use-of-color",TITLE:"Use of Color",DESCRIPTION:"Color is not used as the only visual means of conveying information, indicating an action, prompting a response, or distinguishing a visual element.",DETAILS:{}},"1.4.2":{ID:"audio-control",TITLE:"Audio Control",DESCRIPTION:"If any audio on a Web page plays automatically for more than 3 seconds, either a mechanism is available to pause or stop the audio, or a mechanism is available to control audio volume independently from the overall system volume level.",DETAILS:{}},"1.4.3":{ID:"contrast-minimum",TITLE:"Contrast (Minimum)",DESCRIPTION:"The visual presentation of text and images of text has a contrast ratio of at least 4.5:1, except for the following:",DETAILS:{DETAIL_0:{TITLE:"Large Text",DESCRIPTION:"Large-scale text and images of large-scale text have a contrast ratio of at least 3:1;"},DETAIL_1:{TITLE:"Incidental",DESCRIPTION:"Text or images of text that are part of an inactive user interface component, that are pure decoration, that are not visible to anyone, or that are part of a picture that contains significant other visual content, have no contrast requirement."},DETAIL_2:{TITLE:"Logotypes",DESCRIPTION:"Text that is part of a logo or brand name has no contrast requirement."}}},"1.4.4":{ID:"resize-text",TITLE:"Resize text",DESCRIPTION:"Except for captions and images of text, text can be resized without assistive technology up to 200 percent without loss of content or functionality.",DETAILS:{}},"1.4.5":{ID:"images-of-text",TITLE:"Images of Text",DESCRIPTION:"If the technologies being used can achieve the visual presentation, text is used to convey information rather than images of text except for the following:",DETAILS:{DETAIL_0:{TITLE:"Customizable",DESCRIPTION:"The image of text can be visually customized to the user's requirements;"},DETAIL_1:{TITLE:"Essential",DESCRIPTION:"A particular presentation of text is essential to the information being conveyed."}}},"1.4.6":{ID:"contrast-enhanced",TITLE:"Contrast (Enhanced)",DESCRIPTION:"The visual presentation of text and images of text has a contrast ratio of at least 7:1, except for the following:",DETAILS:{DETAIL_0:{TITLE:"Large Text",DESCRIPTION:"Large-scale text and images of large-scale text have a contrast ratio of at least 4.5:1;"},DETAIL_1:{TITLE:"Incidental",DESCRIPTION:"Text or images of text that are part of an inactive user interface component, that are pure decoration, that are not visible to anyone, or that are part of a picture that contains significant other visual content, have no contrast requirement."},DETAIL_2:{TITLE:"Logotypes",DESCRIPTION:"Text that is part of a logo or brand name has no contrast requirement."}}},"1.4.7":{ID:"low-or-no-background-audio",TITLE:"Low or No Background Audio",DESCRIPTION:"For prerecorded audio-only content that (1) contains primarily speech in the foreground, (2) is not an audio CAPTCHA or audio logo, and (3) is not vocalization intended to be primarily musical expression such as singing or rapping, at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"No Background",DESCRIPTION:"The audio does not contain background sounds."},DETAIL_1:{TITLE:"Turn Off",DESCRIPTION:"The background sounds can be turned off."},DETAIL_2:{TITLE:"20 dB",DESCRIPTION:'The background sounds are at least 20 decibels lower than the foreground speech content, with the exception of occasional sounds that last for only one or two seconds. NotePer the definition of "decibel," background sound that meets this requirement will be approximately four times quieter than the foreground speech content.'}}},"1.4.8":{ID:"visual-presentation",TITLE:"Visual Presentation",DESCRIPTION:"For the visual presentation of blocks of text, a mechanism is available to achieve the following:",DETAILS:{}},"1.4.9":{ID:"images-of-text-no-exception",TITLE:"Images of Text (No Exception)",DESCRIPTION:"Images of text are only used for pure decoration or where a particular presentation of text is essential to the information being conveyed.",DETAILS:{}},"1.4.10":{ID:"reflow",TITLE:"Reflow",DESCRIPTION:"Content can be presented without loss of information or functionality, and without requiring scrolling in two dimensions for:",DETAILS:{}},"1.4.11":{ID:"non-text-contrast",TITLE:"Non-text Contrast",DESCRIPTION:"The visual presentation of the following have a contrast ratio of at least 3:1 against adjacent color(s):",DETAILS:{DETAIL_0:{TITLE:"User Interface Components",DESCRIPTION:"Visual information required to identify user interface components and states, except for inactive components or where the appearance of the component is determined by the user agent and not modified by the author;"},DETAIL_1:{TITLE:"Graphical Objects",DESCRIPTION:"Parts of graphics required to understand the content, except when a particular presentation of graphics is essential to the information being conveyed."}}},"1.4.12":{ID:"text-spacing",TITLE:"Text Spacing",DESCRIPTION:"In content implemented using markup languages that support the following text style properties, no loss of content or functionality occurs by setting all of the following and by changing no other style property:",DETAILS:{}},"1.4.13":{ID:"content-on-hover-or-focus",TITLE:"Content on Hover or Focus",DESCRIPTION:"Where receiving and then removing pointer hover or keyboard focus triggers additional content to become visible and then hidden, the following are true:",DETAILS:{DETAIL_0:{TITLE:"Dismissable",DESCRIPTION:"A mechanism is available to dismiss the additional content without moving pointer hover or keyboard focus, unless the additional content communicates an input error or does not obscure or replace other content;"},DETAIL_1:{TITLE:"Hoverable",DESCRIPTION:"If pointer hover can trigger the additional content, then the pointer can be moved over the additional content without the additional content disappearing;"},DETAIL_2:{TITLE:"Persistent",DESCRIPTION:"The additional content remains visible until the hover or focus trigger is removed, the user dismisses it, or its information is no longer valid."}}},"2.1.1":{ID:"keyboard",TITLE:"Keyboard",DESCRIPTION:"All functionality of the content is operable through a keyboard interface without requiring specific timings for individual keystrokes, except where the underlying function requires input that depends on the path of the user's movement and not just the endpoints.",DETAILS:{}},"2.1.2":{ID:"no-keyboard-trap",TITLE:"No Keyboard Trap",DESCRIPTION:"If keyboard focus can be moved to a component of the page using a keyboard interface, then focus can be moved away from that component using only a keyboard interface, and, if it requires more than unmodified arrow or tab keys or other standard exit methods, the user is advised of the method for moving focus away.",DETAILS:{}},"2.1.3":{ID:"keyboard-no-exception",TITLE:"Keyboard (No Exception)",DESCRIPTION:"All functionality of the content is operable through a keyboard interface without requiring specific timings for individual keystrokes.",DETAILS:{}},"2.1.4":{ID:"character-key-shortcuts",TITLE:"Character Key Shortcuts",DESCRIPTION:"If a keyboard shortcut is implemented in content using only letter (including upper- and lower-case letters), punctuation, number, or symbol characters, then at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"Turn off",DESCRIPTION:"A mechanism is available to turn the shortcut off;"},DETAIL_1:{TITLE:"Remap",DESCRIPTION:"A mechanism is available to remap the shortcut to use one or more non-printable keyboard characters (e.g. Ctrl, Alt, etc);"},DETAIL_2:{TITLE:"Active only on focus",DESCRIPTION:"The keyboard shortcut for a user interface component is only active when that component has focus."}}},"2.2.1":{ID:"timing-adjustable",TITLE:"Timing Adjustable",DESCRIPTION:"For each time limit that is set by the content, at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"Turn off",DESCRIPTION:"The user is allowed to turn off the time limit before encountering it; or"},DETAIL_1:{TITLE:"Adjust",DESCRIPTION:"The user is allowed to adjust the time limit before encountering it over a wide range that is at least ten times the length of the default setting; or"},DETAIL_2:{TITLE:"Extend",DESCRIPTION:'The user is warned before time expires and given at least 20 seconds to extend the time limit with a simple action (for example, "press the space bar"), and the user is allowed to extend the time limit at least ten times; or'},DETAIL_3:{TITLE:"Real-time Exception",DESCRIPTION:"The time limit is a required part of a real-time event (for example, an auction), and no alternative to the time limit is possible; or"},DETAIL_4:{TITLE:"Essential Exception",DESCRIPTION:"The time limit is essential and extending it would invalidate the activity; or"},DETAIL_5:{TITLE:"20 Hour Exception",DESCRIPTION:"The time limit is longer than 20 hours."}}},"2.2.2":{ID:"pause-stop-hide",TITLE:"Pause, Stop, Hide",DESCRIPTION:"For moving, blinking, scrolling, or auto-updating information, all of the following are true:",DETAILS:{DETAIL_0:{TITLE:"Moving, blinking, scrolling",DESCRIPTION:"For any moving, blinking or scrolling information that (1) starts automatically, (2) lasts more than five seconds, and (3) is presented in parallel with other content, there is a mechanism for the user to pause, stop, or hide it unless the movement, blinking, or scrolling is part of an activity where it is essential; and"},DETAIL_1:{TITLE:"Auto-updating",DESCRIPTION:"For any auto-updating information that (1) starts automatically and (2) is presented in parallel with other content, there is a mechanism for the user to pause, stop, or hide it or to control the frequency of the update unless the auto-updating is part of an activity where it is essential."}}},"2.2.3":{ID:"no-timing",TITLE:"No Timing",DESCRIPTION:"Timing is not an essential part of the event or activity presented by the content, except for non-interactive synchronized media and real-time events.",DETAILS:{}},"2.2.4":{ID:"interruptions",TITLE:"Interruptions",DESCRIPTION:"Interruptions can be postponed or suppressed by the user, except interruptions involving an emergency.",DETAILS:{}},"2.2.5":{ID:"re-authenticating",TITLE:"Re-authenticating",DESCRIPTION:"When an authenticated session expires, the user can continue the activity without loss of data after re-authenticating.",DETAILS:{}},"2.2.6":{ID:"timeouts",TITLE:"Timeouts",DESCRIPTION:"Users are warned of the duration of any user inactivity that could cause data loss, unless the data is preserved for more than 20 hours when the user does not take any actions.",DETAILS:{}},"2.3.1":{ID:"three-flashes-or-below-threshold",TITLE:"Three Flashes or Below Threshold",DESCRIPTION:"Web pages do not contain anything that flashes more than three times in any one second period, or the flash is below the general flash and red flash thresholds.",DETAILS:{}},"2.3.2":{ID:"three-flashes",TITLE:"Three Flashes",DESCRIPTION:"Web pages do not contain anything that flashes more than three times in any one second period.",DETAILS:{}},"2.3.3":{ID:"animation-from-interactions",TITLE:"Animation from Interactions",DESCRIPTION:"Motion animation triggered by interaction can be disabled, unless the animation is essential to the functionality or the information being conveyed.",DETAILS:{}},"2.4.1":{ID:"bypass-blocks",TITLE:"Bypass Blocks",DESCRIPTION:"A mechanism is available to bypass blocks of content that are repeated on multiple Web pages.",DETAILS:{}},"2.4.2":{ID:"page-titled",TITLE:"Page Titled",DESCRIPTION:"Web pages have titles that describe topic or purpose.",DETAILS:{}},"2.4.3":{ID:"focus-order",TITLE:"Focus Order",DESCRIPTION:"If a Web page can be navigated sequentially and the navigation sequences affect meaning or operation, focusable components receive focus in an order that preserves meaning and operability.",DETAILS:{}},"2.4.4":{ID:"link-purpose-in-context",TITLE:"Link Purpose (In Context)",DESCRIPTION:"The purpose of each link can be determined from the link text alone or from the link text together with its programmatically determined link context, except where the purpose of the link would be ambiguous to users in general.",DETAILS:{}},"2.4.5":{ID:"multiple-ways",TITLE:"Multiple Ways",DESCRIPTION:"More than one way is available to locate a Web page within a set of Web pages except where the Web Page is the result of, or a step in, a process.",DETAILS:{}},"2.4.6":{ID:"headings-and-labels",TITLE:"Headings and Labels",DESCRIPTION:"Headings and labels describe topic or purpose.",DETAILS:{}},"2.4.7":{ID:"focus-visible",TITLE:"Focus Visible",DESCRIPTION:"Any keyboard operable user interface has a mode of operation where the keyboard focus indicator is visible.",DETAILS:{}},"2.4.8":{ID:"location",TITLE:"Location",DESCRIPTION:"Information about the user's location within a set of Web pages is available.",DETAILS:{}},"2.4.9":{ID:"link-purpose-link-only",TITLE:"Link Purpose (Link Only)",DESCRIPTION:"A mechanism is available to allow the purpose of each link to be identified from link text alone, except where the purpose of the link would be ambiguous to users in general.",DETAILS:{}},"2.4.10":{ID:"section-headings",TITLE:"Section Headings",DESCRIPTION:"Section headings are used to organize the content.",DETAILS:{}},"2.5.1":{ID:"pointer-gestures",TITLE:"Pointer Gestures",DESCRIPTION:"All functionality that uses multipoint or path-based gestures for operation can be operated with a single pointer without a path-based gesture, unless a multipoint or path-based gesture is essential.",DETAILS:{}},"2.5.2":{ID:"pointer-cancellation",TITLE:"Pointer Cancellation",DESCRIPTION:"For functionality that can be operated using a single pointer, at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"No Down-Event",DESCRIPTION:"The down-event of the pointer is not used to execute any part of the function;"},DETAIL_1:{TITLE:"Abort or Undo",DESCRIPTION:"Completion of the function is on the up-event, and a mechanism is available to abort the function before completion or to undo the function after completion;"},DETAIL_2:{TITLE:"Up Reversal",DESCRIPTION:"The up-event reverses any outcome of the preceding down-event;"},DETAIL_3:{TITLE:"Essential",DESCRIPTION:"Completing the function on the down-event is essential."}}},"2.5.3":{ID:"label-in-name",TITLE:"Label in Name",DESCRIPTION:"For user interface components with labels that include text or images of text, the name contains the text that is presented visually.",DETAILS:{}},"2.5.4":{ID:"motion-actuation",TITLE:"Motion Actuation",DESCRIPTION:"Functionality that can be operated by device motion or user motion can also be operated by user interface components and responding to the motion can be disabled to prevent accidental actuation, except when:",DETAILS:{DETAIL_0:{TITLE:"Supported Interface",DESCRIPTION:"The motion is used to operate functionality through an accessibility supported interface;"},DETAIL_1:{TITLE:"Essential",DESCRIPTION:"The motion is essential for the function and doing so would invalidate the activity."}}},"2.5.5":{ID:"target-size",TITLE:"Target Size",DESCRIPTION:"The size of the target for pointer inputs is at least 44 by 44 CSS pixels except when:",DETAILS:{DETAIL_0:{TITLE:"Equivalent",DESCRIPTION:"The target is available through an equivalent link or control on the same page that is at least 44 by 44 CSS pixels;"},DETAIL_1:{TITLE:"Inline",DESCRIPTION:"The target is in a sentence or block of text;"},DETAIL_2:{TITLE:"User Agent Control",DESCRIPTION:"The size of the target is determined by the user agent and is not modified by the author;"},DETAIL_3:{TITLE:"Essential",DESCRIPTION:"A particular presentation of the target is essential to the information being conveyed."}}},"2.5.6":{ID:"concurrent-input-mechanisms",TITLE:"Concurrent Input Mechanisms",DESCRIPTION:"Web content does not restrict use of input modalities available on a platform except where the restriction is essential, required to ensure the security of the content, or required to respect user settings.",DETAILS:{}},"3.1.1":{ID:"language-of-page",TITLE:"Language of Page",DESCRIPTION:"The default human language of each Web page can be programmatically determined.",DETAILS:{}},"3.1.2":{ID:"language-of-parts",TITLE:"Language of Parts",DESCRIPTION:"The human language of each passage or phrase in the content can be programmatically determined except for proper names, technical terms, words of indeterminate language, and words or phrases that have become part of the vernacular of the immediately surrounding text.",DETAILS:{}},"3.1.3":{ID:"unusual-words",TITLE:"Unusual Words",DESCRIPTION:"A mechanism is available for identifying specific definitions of words or phrases used in an unusual or restricted way, including idioms and jargon.",DETAILS:{}},"3.1.4":{ID:"abbreviations",TITLE:"Abbreviations",DESCRIPTION:"A mechanism for identifying the expanded form or meaning of abbreviations is available.",DETAILS:{}},"3.1.5":{ID:"reading-level",TITLE:"Reading Level",DESCRIPTION:"When text requires reading ability more advanced than the lower secondary education level after removal of proper names and titles, supplemental content, or a version that does not require reading ability more advanced than the lower secondary education level, is available.",DETAILS:{}},"3.1.6":{ID:"pronunciation",TITLE:"Pronunciation",DESCRIPTION:"A mechanism is available for identifying specific pronunciation of words where meaning of the words, in context, is ambiguous without knowing the pronunciation.",DETAILS:{}},"3.2.1":{ID:"on-focus",TITLE:"On Focus",DESCRIPTION:"When any user interface component receives focus, it does not initiate a change of context.",DETAILS:{}},"3.2.2":{ID:"on-input",TITLE:"On Input",DESCRIPTION:"Changing the setting of any user interface component does not automatically cause a change of context unless the user has been advised of the behavior before using the component.",DETAILS:{}},"3.2.3":{ID:"consistent-navigation",TITLE:"Consistent Navigation",DESCRIPTION:"Navigational mechanisms that are repeated on multiple Web pages within a set of Web pages occur in the same relative order each time they are repeated, unless a change is initiated by the user.",DETAILS:{}},"3.2.4":{ID:"consistent-identification",TITLE:"Consistent Identification",DESCRIPTION:"Components that have the same functionality within a set of Web pages are identified consistently.",DETAILS:{}},"3.2.5":{ID:"change-on-request",TITLE:"Change on Request",DESCRIPTION:"Changes of context are initiated only by user request or a mechanism is available to turn off such changes.",DETAILS:{}},"3.3.1":{ID:"error-identification",TITLE:"Error Identification",DESCRIPTION:"If an input error is automatically detected, the item that is in error is identified and the error is described to the user in text.",DETAILS:{}},"3.3.2":{ID:"labels-or-instructions",TITLE:"Labels or Instructions",DESCRIPTION:"Labels or instructions are provided when content requires user input.",DETAILS:{}},"3.3.3":{ID:"error-suggestion",TITLE:"Error Suggestion",DESCRIPTION:"If an input error is automatically detected and suggestions for correction are known, then the suggestions are provided to the user, unless it would jeopardize the security or purpose of the content.",DETAILS:{}},"3.3.4":{ID:"error-prevention-legal-financial-data",TITLE:"Error Prevention (Legal, Financial, Data)",DESCRIPTION:"For Web pages that cause legal commitments or financial transactions for the user to occur, that modify or delete user-controllable data in data storage systems, or that submit user test responses, at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"Reversible",DESCRIPTION:"Submissions are reversible."},DETAIL_1:{TITLE:"Checked",DESCRIPTION:"Data entered by the user is checked for input errors and the user is provided an opportunity to correct them."},DETAIL_2:{TITLE:"Confirmed",DESCRIPTION:"A mechanism is available for reviewing, confirming, and correcting information before finalizing the submission."}}},"3.3.5":{ID:"help",TITLE:"Help",DESCRIPTION:"Context-sensitive help is available.",DETAILS:{}},"3.3.6":{ID:"error-prevention-all",TITLE:"Error Prevention (All)",DESCRIPTION:"For Web pages that require the user to submit information, at least one of the following is true:",DETAILS:{DETAIL_0:{TITLE:"Reversible",DESCRIPTION:"Submissions are reversible."},DETAIL_1:{TITLE:"Checked",DESCRIPTION:"Data entered by the user is checked for input errors and the user is provided an opportunity to correct them."},DETAIL_2:{TITLE:"Confirmed",DESCRIPTION:"A mechanism is available for reviewing, confirming, and correcting information before finalizing the submission."}}},"4.1.1":{ID:"parsing",TITLE:"Parsing",DESCRIPTION:"In content implemented using markup languages, elements have complete start and end tags, elements are nested according to their specifications, elements do not contain duplicate attributes, and any IDs are unique, except where the specifications allow these features.",DETAILS:{}},"4.1.2":{ID:"name-role-value",TITLE:"Name, Role, Value",DESCRIPTION:"For all user interface components (including but not limited to: form elements, links and components generated by scripts), the name and role can be programmatically determined; states, properties, and values that can be set by the user can be programmatically set; and notification of changes to these items is available to user agents, including assistive technologies.",DETAILS:{}},"4.1.3":{ID:"status-messages",TITLE:"Status Messages",DESCRIPTION:"In content implemented using markup languages, status messages can be programmatically determined through role or properties such that they can be presented to the user by assistive technologies without receiving focus.",DETAILS:{}}}}},a={UI:e,PAGES:t,WCAG:o};export default a;export{t as PAGES,e as UI,o as WCAG};
